import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function DashboardPage() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  const getRandomAvatar = () => {
    // Puoi sostituire con link reali o immagini fantasy future
    const index = Math.floor(Math.random() * 3) + 1;
    return `https://api.dicebear.com/7.x/adventurer/svg?seed=Hero${index}`;
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-black text-white px-4">
      <h1 className="text-4xl font-bold mb-6">Welcome, Adventurer</h1>
      {user && (
        <div className="bg-gray-800 rounded-xl p-6 w-full max-w-md text-center shadow-lg">
          <img
            src={getRandomAvatar()}
            alt="avatar"
            className="w-24 h-24 mx-auto rounded-full mb-4"
          />
          <p className="text-xl font-semibold mb-1">{user.email}</p>
          <p className="text-yellow-400 mb-4">Level 3 â€¢ Novice Warrior</p>
          <button className="bg-yellow-500 hover:bg-yellow-400 text-black font-bold py-2 px-4 rounded mb-4 transition">
            Inizia il Dungeon
          </button>
          <button
            onClick={handleLogout}
            className="text-sm text-red-400 hover:underline"
          >
            Log Out
          </button>
        </div>
      )}
    </div>
  );
}
